$(document).ready(function($){var a=window.location.href,e=a.lastIndexOf(".html"),s=a.lastIndexOf("/")+1;switch(a=a.substring(s,e)){case"about":$("#about-nav li").toggleClass("active",!0);break;case"service":$("#service-nav li").toggleClass("active",!0);break;case"edu":$("#edu-nav li").toggleClass("active",!0);break;case"awards":$("#awards-nav li").toggleClass("active",!0);break;default:$("#index-nav li").toggleClass("active",!0)}$(".mobile-button").on("click",function(){$("nav").toggleClass("menu-open")})});